import Title from "../Tittle";
import styles from "./styles.module.css";

const Sale = ({ products, link = false }) => {
  const countDiscount = (price, discountPrice) => {
    return Math.round(((price - discountPrice) / price) * 100);
  };

  return (
    <section className={styles.saleSection}>
      <Title title="Sale" link={link} />
      {products ? (
        <div className={styles.productsContainer}>
          {products.map((product) => {
            return (
              <div key={product.id} className={styles.productCard}>
                <img
                  className={styles.imageCategory}
                  src={product.image}
                  alt={`product ${product.title}`}
                />
                <div className={styles.blueBox}>
                  {`-${countDiscount(product.price, product.discontPrice)}%`}
                </div>
                <h5 className={styles.titleProduct}>product.title</h5>
                <p className={styles.priceProduct}>product.price</p>
                <h6 className={styles.discontPriceProduct}>
                  {product.discontPrice}
                </h6>
              </div>
            );
          })}
        </div>
      ) : (
        <p className={styles.emptyProducts}>No discount products</p>
      )}
    </section>
  );
};
export default Sale;
