import styles from "./styles.module.css";
import { useForm } from "react-hook-form";
import petsImg from "../../assets/img/FirstOrderImg.jpg";

const Main = () => {
  const {
    register,
    handleSubmit,
    formState: { errors },
  } = useForm();
  const onSubmit = (data) => console.log(data);
  console.log(errors);

  return (
    <div className={styles.mainContainer}>
      <section className={styles.discountContainer}>
        <h2 className={styles.title}>Amazing Discounts on Pets Products!</h2>
        <button className={styles.accentButton}>Check out</button>
      </section>

      <section className={styles.firstOrderContainer}>
        <h3 className={styles.titleOrder}>5% off on the first order</h3>
        <div className={styles.firstOrderContentContainer}>
          <img src={petsImg} alt="pets" />
          <form
            className={styles.formContainer}
            onSubmit={handleSubmit(onSubmit)}
          >
            <input
              type="text"
              placeholder="Name"
              {...register("firstName", {
                required: true,
                min: 1,
                maxLength: 80,
              })}
            />
            <input
              type="tel"
              placeholder="Number"
              {...register("number", {
                required: true,
                minLength: 6,
                maxLength: 12,
              })}
            />
            <input
              type="text"
              placeholder="Email"
              {...register("Email", {
                required: true,
                min: 1,
                pattern: /^\S+@\S+$/i,
              })}
            />

            <button className={styles.secondaryButton} type="submit">
              Check out
            </button>
          </form>
        </div>
      </section>
    </div>
  );
};

export default Main;
